import { useState, useEffect } from 'react';

function useLocalStorage() {
  const [storage, setStorage] = useState(localStorage);

  useEffect(() => {
    const handleStorageChange = () => {
      // Update the storage state with the latest localStorage values
      setStorage(localStorage);
    };

    // Listen for storage events (e.g., changes in localStorage from other tabs)
    window.addEventListener('storage', handleStorageChange);

    // Clean up the event listener on component unmount
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []); // Effect runs only once on component mount

  return storage;
}

export default useLocalStorage;
