import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import ImageSlider from "../organisms/ImageSlider";
import UsernameInput from "../molecules/UsernameInput";
import logoImage from '../assets/images/logo.png';

function UserCreate() {
  const [username, setUsername] = useState('');
  const history = useHistory(); // Accessing the history object

  // Function to update the username state and redirect to game selection
  const handleUsernameSubmit = (newUsername) => {
    setUsername(newUsername);
    localStorage.setItem('username', newUsername); // Storing username in localStorage
    history.push('/game-selection'); // Redirecting to the game selection page
  };

  // Render UsernameInput only if username is not set
  if (!username) {
    return (
      <div className="user-create-container">
        <h2 className="user-create-container__subtitle">Multiplayer</h2>
        <img src={logoImage} alt="Logo" />
        <ImageSlider />
        <UsernameInput onSubmit={handleUsernameSubmit} />
      </div>
    );
  } else {
    // If username is set, render null
    return null;
  }
}

export default UserCreate;
